import React from 'react';

const user = ({ name, age }) => (
    <div>
        <h1>Name: {name}</h1>
        <h1>Age: {age}</h1>
        <style jsx>{`
            div {
                border: 1px solid #eee;
                box-shadow: 0 2px 3px #ccc;
                padding: 20px;
                text-align: center;
            } 
        `}</style>
    </div>
);

export default user;
